import React from "react";
import styles from "./Cart.module.css";
import Modal from "../UI/Modal";

const Cart = (props) => {
  const cartItem = (
    <ul className={styles["cart-items"]}>
      {[{ id: "c1", name: "Chicken Tandoori", amount: 2, price: "250" }].map(
        (item) => {
          return <li>{item.name}</li>;
        }
      )}
    </ul>
  );
  return (
    <Modal>
      {cartItem}
      <div className={styles.total}>
        <span>Amount</span>
        <span>280</span>
      </div>
      <div className={styles.actions}>
        <button className={styles["button--alt"]} onClick={props.onHideCart}>Cancel</button>
        <button className={styles.button}>Order</button>
      </div>
    </Modal>
  );
};
export default Cart;
